mapper.select:
selet uid, upw
from t_parent
where uid = #{uid} and upw = #{upw}

select upw
from t_parent
where uid =#{uid}

select ikid
from t_parent_kid
where ikid = #{ikid}

mapper.insert:
insert into t_parent
set parent_nm = #{parentNm}, uid = #{uid}, upw = #{upw},
phone_nb = #{phoneNb}, irelation = #{irelation}

mapper.update:
update t_parent
set parent_nm = #{parentNm}, phone_nb = #{phoneNb},
address = #{address}, upw = #{upw},
irank = #{irank}
where iparent = #{iparent}
